mapper.select:
SELECT 
	FROM
	(
		SELECT ROWNUM RNUM, 
		FROM(

			ORDER BY BOARD_NOTICE_DATE DESC
			)
	WHERE ROWNUM <=#{endIndex}
	)
WHERE RNUM>=#{startIndex}

SELECT COUNT(*)
FROM BOARD_NOTICE
WHERE BOARE_NOTICE_ITEMS LIKE #{items}

SELECT FROM ( SELECT ROWNUM RNUM, FROM (
ORDER BY BOARD_NOTICE_DATE DESC
ORDER BY BOARD_NOTICE_DATE DESC
WHERE BOARE_NOTICE_ITEMS LIKE '%'||#{items}||'%' ORDER BY BOARD_NOTICE_DATE DESC
) WHERE ROWNUM <=#{endIndex} ) WHERE RNUM>=#{startIndex}
